import 'dart:async';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:my_cart/page/cart/cart_page.dart';class SpleshPage extends StatefulWidget {  const SpleshPage({super.key});  @override  State<SpleshPage> createState() => _SpleshPageState();}class _SpleshPageState extends State<SpleshPage> {  @override  void initState() {    // TODO: implement initState    super.initState();    Timer(      Duration(seconds: 2),      () {        Navigator.pushReplacement(          context,          MaterialPageRoute(            builder: (context) => CartPage(),          ),        );      },    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(),      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.spaceAround,          children: [            Column(              children: <Widget>[                Container(                  child: Image(                    width: 250,                    height: 250,                    image: AssetImage("assets/images/main.png"),                  ),                ),                Container(                  child: Text(                    'Flutter Enthusiast | UI Craftsman',                    style: TextStyle(                      fontFamily: 'PlusR',                      fontSize: 20,                    ),                  ),                ),              ],            ),            InkWell(              onTap: () {                Navigator.push(                  context,                  MaterialPageRoute(                    builder: (context) => CartPage(),                  ),                );              },              child: Container(                decoration: BoxDecoration(                  borderRadius: BorderRadius.circular(100),                  color: Colors.deepPurpleAccent.withOpacity(0.2),                ),                child: Padding(                  padding: const EdgeInsets.all(15),                  child: Icon(                    CupertinoIcons.arrow_right,                  ),                ),              ),            ),          ],        ),      ),    );  }}